com.mirth.connect.plugins.javascriptstep.script:
var mapping;

function toDateFr(dmp)
{

var an=dmp.substring(0,4);
var mois=dmp.substring(4,6);
var jour=dmp.substring(6,8);

	return jour+"-"+mois+"-"+an;
}

try {
    mapping = msg['PID']['PID.7']['PID.7.1'].toString();
} catch (e) {
    logger.error(e);
    mapping = '';
}

channelMap.put('DDN', validate(toDateFr(mapping), '', new Array()));

var strCRLF = "\r\n"; 
channelMap.put('strCRLF', strCRLF);

return true;

var mapping;

function toDateMariaDB(dmp)
{

var an=dmp.substring(0,4);
var mois=dmp.substring(4,6);
var jour=dmp.substring(6,8);

	return an+"-"+mois+"-"+jour;
}

try {
    mapping = msg['PID']['PID.7']['PID.7.1'].toString();
} catch (e) {
    logger.error(e);
    mapping = '';
}

channelMap.put('DDNMDB', validate(toDateMariaDB(mapping), '', new Array()));

return true;

