com.mirth.connect.plugins.javascriptstep.script:
var IPP;

try {
    IPP = msg['PID']['PID.3']['PID.3.1'].toString();
} catch (e) {
    logger.error(e);
    IPP = '0';
}

var NOM;

try {
    NOM = msg['PID']['PID.5'][0]['PID.5.1'].toString();
} catch (e) {
    logger.error(e);
    NOM = 'Non renseigné';
}

var PRENOM;

try {
    PRENOM = msg['PID']['PID.5'][0]['PID.5.2'].toString();
} catch (e) {
    logger.error(e);
    PRENOM = 'Non Renseigné';
}

//creation de la variable document

var DOC;
DOC= "--FICHE D 'ADMISSION DU PATIENT--<br/>";
DOC=DOC + 'Numero de dossier du patient : ' + IPP + '<br/>\n';
DOC=DOC + 'Nom du patient : ' + NOM +'<br/>\n';
DOC=DOC + 'Prenom du patient : ' + PRENOM +'<br/>';

connectorMap.put('DOC',DOC);

return true;

